    public static class PHHelper
    {
        #region Texture Save in Code

        //base64 Textures
        private static string blueTextureCode =
            "";

        private static Texture2D _blueTexture;

        public static Texture2D blueTexture
        {
            get
            {
                if (_blueTexture == null)
                    _blueTexture = Base64ToTexture(blueTextureCode);
                return _blueTexture;
            }
        }

        //convert base64 to texture
        public static Texture2D Base64ToTexture(string base64Str)
        {
            Texture2D texture = new Texture2D(0, 0, TextureFormat.ARGB32, false, false);
            texture.hideFlags = HideFlags.HideAndDontSave;
            texture.LoadImage(Convert.FromBase64String(base64Str));
            return texture;
        }

        #endregion



        private static GUISkin _guiSkin;

        public static GUISkin GetGUISkin()
        {
            if (_guiSkin == null)
                _guiSkin = Resources.Load<GUISkin>("PluginHubGUISkin");
            return _guiSkin;
        }


        /// <summary>
        /// 运行时打开文件浏览器并导航到一个文件夹 如果isCreate为真，则当path不存在的时候会自动创建
        /// </summary>
        public static void OpenFileExplorer(string directory, bool isCreate = false)
        {
            if (!Directory.Exists(directory))
            {
                //如果目录不存在 创建之
                if (isCreate)
                {
                    Debug.Log($"Make dir {directory}");
                    Directory.CreateDirectory(directory);
                }
                else
                {
                    Debug.LogWarning(directory + " not exist");
                    return;
                }
            }

            string args = $"/Select, {directory}";
            args = args.Replace("/", "\\");
            ProcessStartInfo pfi = new ProcessStartInfo("Explorer.exe", args);
            Process.Start(pfi);

            //该代码也是打开文件管理器，但是打开的目录在指向目录的上一层，稳定就行，不要随意换。
            //不支持D:\UnityProject\TopWellCustomPattern\Assets\..\Build\ 这种带..的路径
            //EditorUtility.RevealInFinder(path);
        }

        ///执行可执行文件
        ///
        public static void ExecuteExe(string exeFullPath, bool admin = false)
        {
            //Process.Start只支持windows的路径格式（左斜线分隔）
            exeFullPath = exeFullPath.Replace('/', '\\');
            if (!exeFullPath.EndsWith(".exe"))
            {
                Debug.LogError("ExecuteExe only support .exe");
                return;
            }

            if (!File.Exists(exeFullPath))
            {
                Debug.LogError($"{exeFullPath} do not exist");
                return;
            }

            string executeFileName = Path.GetFileName(exeFullPath); //文件名  xxx.exe
            string workingDirectory = Path.GetDirectoryName(exeFullPath); //文件所在目录  D:\xxx\

            Process proc = new Process();
            proc.StartInfo.WorkingDirectory = workingDirectory;
            proc.StartInfo.FileName = executeFileName;
            if (admin)
                proc.StartInfo.Verb = "runas"; //使用管理员运行
            proc.Start();
            Debug.Log($"Run exe {exeFullPath}");
        }


        private static TextEditor te = new TextEditor();

        ///拷贝指定字符串到剪贴板
        public static void CopyToClipboard(string str)
        {
            te.text = str;
            te.OnFocus();
            te.Copy();
            te.text = string.Empty;
        }

        //获取设备的mac地址，IOS和Android不支持
        public static string DeviceMac()
        {
            string physicalAddress = "";
            NetworkInterface[] nice = NetworkInterface.GetAllNetworkInterfaces();
            foreach (NetworkInterface adaper in nice)
            {
                physicalAddress = adaper.GetPhysicalAddress().ToString();
                // Debug.Log(physicalAddress);
                // Debug.Log(adaper.Id);
                // Debug.Log(adaper.Name);
                // Debug.Log(adaper.Speed);
                // Debug.Log(adaper.NetworkInterfaceType);
                // Debug.Log(adaper.OperationalStatus);
                // Debug.Log(adaper.SupportsMulticast);
                // Debug.Log(adaper.IsReceiveOnly);
                // Debug.Log(adaper.Description);
                if (!string.IsNullOrWhiteSpace(physicalAddress))
                    break;
            }

            return physicalAddress;
        }
    }
